import React, { Component } from "react";
import {
  Tabs,
  Tab,
  Grid,
  Cell,
  Card,
  CardTitle,
  CardText,
  CardActions,
  Button,
} from "react-mdl";

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = { activeTab: 0 };
  }

  toggleCategories() {
    if (this.state.activeTab === 0) {
      return (
        <div className="projects-grid">
          {/* Project 1 */}
          <Card shadow={5} style={{ width: "300px", margin: "auto" }}>
            <CardTitle
              style={{
                height: "150px",
                width: "150px",
                background:
                  "url(data:image/png;base64,) center / cover",
              }}
            ></CardTitle>

            <CardText>
              <h3>GitHub Finder</h3>
              <p>This is a full stack React app to search Github profiles</p>
            </CardText>

            <CardActions border>
              <Button
                href="https://github.com/Luyanda-Tommy27/GITHUB-FINDER-PROJECT"
                target="blank"
                colored
              >
                Go to Repo
              </Button>
              <Button
                href="https://githubfinder2705.netlify.app/"
                target="blank"
              >
                Go to App
              </Button>
            </CardActions>
          </Card>

          {/* Project 2 */}
          <Card shadow={5} style={{ width: "300px", margin: "auto" }}>
            <CardTitle
              style={{
                height: "150px",
                width: "150px",
                background:
                  "url(data:image/png;base64,) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h3>Contact Keeper</h3>
              <p>
                This is a personal database system to store information about
                your friends, family and colleges in one central place
              </p>
            </CardText>
            <CardActions border>
              <Button
                href="https://github.com/Luyanda-Tommy27/ContactKeeper"
                target="blank"
                colored
              >
                Go to Repo
              </Button>
            </CardActions>
          </Card>

          {/* Project 3 */}
          <Card shadow={5} style={{ width: "300px", margin: "auto" }}>
            <CardTitle
              style={{
                height: "150px",
                width: "150px",
                background:
                  "url(data:image/png;base64,) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h3>IT Logger</h3>
              <p>
                A computer app for making a systematic recording of events,
                observations, or measurements
              </p>
            </CardText>
            <CardActions border>
              <Button
                href="https://github.com/Luyanda-Tommy27/IT-Logger"
                target="blank"
              >
                Go to Repo
              </Button>
              <Button href="https://itlogger808.netlify.app/" target="blank">
                Go to App
              </Button>
            </CardActions>
          </Card>
        </div>
      );
    }
  }

  render() {
    return (
      <div>
        <Tabs
          activeTab={this.state.activeTab}
          onChange={(tabId) => this.setState({ activeTab: tabId })}
          ripple
        >
          <Tab>
            <h5>Projects</h5>
          </Tab>
        </Tabs>

        <Grid>
          <Cell col={12}>
            <div className="content">{this.toggleCategories()}</div>
          </Cell>
        </Grid>
      </div>
    );
  }
}

export default Projects;
